package JavaCocepts;

class sitch
{
	void sivh(int input)
	{
		

	 switch(input)
	{
	case 1: System.out.println("Hi it is 1");
	break;
	case 2: System.out.println("Hi it is 2");
	break;
	case 3: System.out.println("Hi it is 3");
	break;
	case 4: System.out.println("Hi it is 4");
	break;
	case 5: System.out.println("Hi it is 5");
	break;
	case 6: System.out.println("Hi it is 6");
	break;
	default: System.out.println("Invalid input");
	break;
}
}
}




public class Switchcase {
	
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 
		sitch ch = new sitch();
		ch.sivh(42);
				
		
		

	}

}
